mapper.select:
		SELECT G.*
		  FROM (  SELECT ROWNUM RNUM, goods_name, goods_kind, goods_price, 
		                 goods_amount, goods_content, goods_discount, goods_date
        		    FROM goods

				   WHERE goods_kind = #{selectedKind}

				ORDER BY RNUM ASC
      		    ) G
		 WHERE G.rnum BETWEEN #{startRow} AND #{endRow}
