mapper.select:
SELECT E.* FROM ( SELECT ROWNUM RNUM, qna_code, qna_title, qna_content FROM qna WHERE 1 = 1
AND qna_title LIKE '%' || #{qnaName} || '%'
) E WHERE E.rnum BETWEEN #{startRow} AND #{endRow} ORDER BY RNUM

SELECT qna_code, qna_title, qna_content
FROM qna
WHERE qna_code = #{qnaCode}

mapper.update:
UPDATE qna
SET qna_title = #{qnaTitle}, qna_content = #{qnaContent}
WHERE qna_code = #{qnaCode}
